import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';

// Importar todos los componentes
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import DashboardPadre from './components/DashboardPadre';
import DashboardDocente from './components/DashboardDocente';
import DashboardAdministrador from './components/DashboardAdministrador';

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Routes>
            {/* Rutas públicas */}
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            
            {/* Ruta raíz redirige a dashboard */}
            <Route path="/" element={<Navigate to="/dashboard" replace />} />
            
            {/* Dashboard general - determina redirección */}
            <Route 
              path="/dashboard" 
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              } 
            />
            
            {/* Dashboards específicos */}
            <Route 
              path="/dashboard-padre" 
              element={
                <ProtectedRoute allowedRoles={['padre', 'parent']}>
                  <DashboardPadre />
                </ProtectedRoute>
              } 
            />
            
            <Route 
              path="/dashboard-docente" 
              element={
                <ProtectedRoute allowedRoles={['docente', 'teacher']}>
                  <DashboardDocente />
                </ProtectedRoute>
              } 
            />
            
            <Route 
              path="/dashboard-administrador" 
              element={
                <ProtectedRoute allowedRoles={['administrador', 'admin', 'administrator']}>
                  <DashboardAdministrador />
                </ProtectedRoute>
              } 
            />
            
            {/* Ruta 404 */}
            <Route path="*" element={<Navigate to="/dashboard" replace />} />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
