import React, { useState } from 'react';
import '../index.css'; // Asegúrate que esté importado tu Tailwind CSS aquí o en index.js

function Register() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'asistente',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      alert('Las contraseñas no coinciden');
      return;
    }
    console.log('Datos enviados:', formData);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#f5f5f5]">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded-xl shadow-xl w-full max-w-md border border-[#e5e7eb]"
      >
        <h2 className="text-3xl font-bold text-center mb-6 text-[#d93025]">
          Crear Cuenta
        </h2>

        <input
          type="text"
          name="name"
          placeholder="Nombre completo"
          value={formData.name}
          onChange={handleChange}
          className="w-full mb-4 p-3 border border-[#e5e7eb] rounded-md focus:outline-none focus:ring-2 focus:ring-[#d93025] focus:border-[#d93025] transition-colors duration-300"
          required
        />

        <input
          type="email"
          name="email"
          placeholder="Correo electrónico"
          value={formData.email}
          onChange={handleChange}
          className="w-full mb-4 p-3 border border-[#e5e7eb] rounded-md focus:outline-none focus:ring-2 focus:ring-[#d93025] focus:border-[#d93025] transition-colors duration-300"
          required
        />

        <input
          type="password"
          name="password"
          placeholder="Contraseña"
          value={formData.password}
          onChange={handleChange}
          className="w-full mb-4 p-3 border border-[#e5e7eb] rounded-md focus:outline-none focus:ring-2 focus:ring-[#d93025] focus:border-[#d93025] transition-colors duration-300"
          required
        />

        <input
          type="password"
          name="confirmPassword"
          placeholder="Confirmar contraseña"
          value={formData.confirmPassword}
          onChange={handleChange}
          className="w-full mb-4 p-3 border border-[#e5e7eb] rounded-md focus:outline-none focus:ring-2 focus:ring-[#d93025] focus:border-[#d93025] transition-colors duration-300"
          required
        />

        <select
          name="role"
          value={formData.role}
          onChange={handleChange}
          className="w-full mb-4 p-3 border border-[#e5e7eb] rounded-md focus:outline-none focus:ring-2 focus:ring-[#d93025] focus:border-[#d93025] transition-colors duration-300"
        >
          <option value="asistente">Asistente</option>
          <option value="docente">Docente</option>
          <option value="coordinador">Coordinador</option>
          <option value="padre">Padre de Familia</option>
        </select>

        <button
          type="submit"
          className="w-full bg-[#d93025] text-white py-3 rounded-md hover:bg-[#b3201a] transition-colors font-semibold focus:outline-none focus:ring-2 focus:ring-[#b3201a] focus:ring-offset-2"
        >
          Registrarse
        </button>
      </form>
    </div>
  );
}

export default Register;
