import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const { isAuthenticated, user, isLoading } = useAuth();

  // Mostrar loading mientras se verifica la autenticaci√≥n
  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-content">
          <div className="loading-spinner"></div>
          <p>Verificando acceso...</p>
        </div>
      </div>
    );
  }

  // Si no est√° autenticado, redirigir al login
  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  // Si hay roles permitidos y el usuario no tiene el rol correcto
  if (allowedRoles.length > 0 && !allowedRoles.includes(user.role)) {
    return (
      <div className="unauthorized-container">
        <div className="unauthorized-content">
          <div className="unauthorized-icon">üö´</div>
          <h2>Acceso Restringido</h2>
          <p>No tienes permisos para acceder a esta secci√≥n.</p>
          <div className="role-info">
            <p><strong>Tu rol actual:</strong> {user.role}</p>
            <p><strong>Roles permitidos:</strong> {allowedRoles.join(', ')}</p>
          </div>
          <div className="unauthorized-actions">
            <button 
              onClick={() => window.history.back()} 
              className="back-button"
            >
              ‚Üê Volver
            </button>
            <Navigate to="/" replace />
          </div>
        </div>
      </div>
    );
  }

  // Si todo est√° bien, mostrar el componente
  return children;
};

export default ProtectedRoute;
