import React from 'react';
import './Hero.css';

function Hero() {
  return (
    <section className="hero">
      <h1>Conectando padres y docentes para el éxito estudiantil</h1>
      <p>
        Programa citas de manera sencilla, mantén comunicación efectiva y haz
        seguimiento al progreso académico de tus estudiantes o hijos.
      </p>
      <button className="cta-button">Programa una cita ahora</button>
    </section>
  );
}

export default Hero;
