import React, { useState, useEffect } from 'react';
import '../css/DashboardDocente.css';
import { useAuth } from '../context/AuthContext';
import {
  CalendarIcon,
  UserIcon,
  ClockIcon,
  DocumentTextIcon,
  ArrowRightOnRectangleIcon,
  PencilIcon,
  TrashIcon,
  CheckIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';
// ðŸ”¥ IMPORTAR FIRESTORE
import { 
  collection, 
  doc, 
  setDoc, 
  getDoc, 
  addDoc, 
  updateDoc, 
  deleteDoc, 
  query, 
  where, 
  getDocs,
  orderBy 
} from 'firebase/firestore';
import { db } from '../firebase/firebaseConfig'; // Ajusta la ruta segÃºn tu estructura

function DashboardDocente() {
  const [activeSection, setActiveSection] = useState('calendario');
  const { user, logout } = useAuth();

  // ðŸ”¥ DATOS DINÃMICOS DEL DOCENTE
  const docenteData = {
    name: user?.name || 'Docente',
    email: user?.email || '',
    subject: user?.materia || user?.subject || 'Materia no asignada',
    grade: user?.grado || user?.grade || 'Grado no asignado',
    phone: user?.telefono || user?.phone || 'No disponible'
  };

  // ðŸ”„ ESTADOS PARA HORARIOS Y CITAS (Cambio a horarios por hora completa)
  const [horarioDocente, setHorarioDocente] = useState({
    lunes: {},
    martes: {},
    miercoles: {},
    jueves: {},
    viernes: {}
  });

  const [citasSemanaActual, setCitasSemanaActual] = useState([]);
  const [historialCitas, setHistorialCitas] = useState([]);
  const [semanaActual, setSemanaActual] = useState('');
  
  // ðŸ‘¥ NUEVO: Estados para usuarios/padres
  const [usuariosPadres, setUsuariosPadres] = useState([]);
  const [loadingUsuarios, setLoadingUsuarios] = useState(false);
  
  // Estados para modales
  const [showNewCitaModal, setShowNewCitaModal] = useState(false);
  const [showEditHorarioModal, setShowEditHorarioModal] = useState(false);
  const [showEditCitaModal, setShowEditCitaModal] = useState(false);
  const [selectedCita, setSelectedCita] = useState(null);
  
  // ðŸ”„ CAMPO ESTUDIANTE REMOVIDO
  const [newCita, setNewCita] = useState({
    padreId: '',
    dia: '',
    hora: '',
    motivo: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const diasSemana = ['lunes', 'martes', 'miercoles', 'jueves', 'viernes'];
  
  // ðŸ• HORARIOS DISPONIBLES (8:00 AM - 2:00 PM, cada hora)
  const horasDisponibles = [
    '08:00', '09:00', '10:00', '11:00', '12:00', '13:00', '14:00'
  ];

  // ðŸ”¥ FUNCIONES FIRESTORE PARA USUARIOS/PADRES

  const cargarUsuariosPadres = async () => {
    try {
      setLoadingUsuarios(true);
      console.log('ðŸ” Buscando usuarios padres en colecciÃ³n "usuarios"...');
      
      const usuariosRef = collection(db, 'usuarios'); // âœ… ColecciÃ³n correcta
      const q = query(usuariosRef, where('role', '==', 'padre')); // âœ… Campo 'role' no 'rol'
      
      const usuariosSnap = await getDocs(q);
      const padres = [];
      
      console.log(`ðŸ“Š Documentos encontrados: ${usuariosSnap.size}`);
      
      usuariosSnap.forEach((doc) => {
        const userData = { id: doc.id, ...doc.data() };
        console.log('ðŸ‘¤ Usuario encontrado:', userData);
        padres.push(userData);
      });
      
      setUsuariosPadres(padres);
      console.log('âœ… Usuarios padres cargados:', padres.length, 'padres encontrados');
      
      if (padres.length === 0) {
        console.log('âš ï¸ No se encontraron usuarios con role="padre"');
        // Intentar bÃºsqueda alternativa con 'rol'
        await buscarConRolAlternativo();
      }
      
    } catch (error) {
      console.error('âŒ Error al cargar usuarios padres:', error);
      setError('Error al cargar usuarios: ' + error.message);
    } finally {
      setLoadingUsuarios(false);
    }
  };

  // ðŸ”„ FUNCIÃ“N ALTERNATIVA PARA BUSCAR CON 'rol' EN LUGAR DE 'role'
  const buscarConRolAlternativo = async () => {
    try {
      console.log('ðŸ” Intentando bÃºsqueda alternativa con campo "rol"...');
      
      const usuariosRef = collection(db, 'usuarios');
      const q = query(usuariosRef, where('rol', '==', 'padre')); // Campo 'rol'
      
      const usuariosSnap = await getDocs(q);
      const padres = [];
      
      console.log(`ðŸ“Š Documentos encontrados con 'rol': ${usuariosSnap.size}`);
      
      usuariosSnap.forEach((doc) => {
        const userData = { id: doc.id, ...doc.data() };
        console.log('ðŸ‘¤ Usuario encontrado (rol):', userData);
        padres.push(userData);
      });
      
      if (padres.length > 0) {
        setUsuariosPadres(padres);
        console.log('âœ… Usuarios encontrados con campo "rol":', padres.length);
      } else {
        // Si tampoco encuentra con 'rol', intentar cargar todos y filtrar
        await cargarTodosLosUsuarios();
      }
      
    } catch (error) {
      console.error('âŒ Error en bÃºsqueda alternativa:', error);
    }
  };

  // ðŸ” FUNCIÃ“N DE ÃšLTIMO RECURSO: CARGAR TODOS Y FILTRAR
  const cargarTodosLosUsuarios = async () => {
    try {
      console.log('ðŸ” Cargando todos los usuarios para debug...');
      
      const usuariosRef = collection(db, 'usuarios');
      const usuariosSnap = await getDocs(usuariosRef);
      
      console.log(`ðŸ“Š Total documentos en colecciÃ³n: ${usuariosSnap.size}`);
      
      const todosUsuarios = [];
      usuariosSnap.forEach((doc) => {
        const userData = { id: doc.id, ...doc.data() };
        todosUsuarios.push(userData);
        console.log('ðŸ“‹ Usuario:', userData);
      });
      
      // Filtrar padres manualmente
      const padres = todosUsuarios.filter(usuario => 
        usuario.role === 'padre' || usuario.rol === 'padre'
      );
      
