      setError('Error al guardar horario: ' + error.message);
      throw error;
    } finally {
      setLoading(false);
    }
  };

  const inicializarHorarioDefault = () => {
    const horarioDefault = {};
    diasSemana.forEach(dia => {
      horarioDefault[dia] = {};
      horasDisponibles.forEach(hora => {
        // Por defecto, todas las horas estÃ¡n disponibles excepto el receso (10:00-11:00)
        horarioDefault[dia][hora] = hora !== '10:00'; // 10:00 es receso
      });
    });
    return horarioDefault;
  };

  // ðŸ”¥ FUNCIONES FIRESTORE PARA CITAS

  const cargarCitasDesdeFirestore = async () => {
    try {
      const citasRef = collection(db, 'citas');
      
      // ðŸ”§ CONSULTA SIMPLIFICADA (sin orderBy) para evitar error de Ã­ndice
      const q = query(
        citasRef, 
        where('docenteId', '==', user.uid),
        where('semana', '==', obtenerSemanaActual())
      );
      
      const citasSnap = await getDocs(q);
      const citas = [];
      
      citasSnap.forEach((doc) => {
        citas.push({
          id: doc.id,
          ...doc.data()
        });
      });
      
      // ðŸ”„ ORDENAR MANUALMENTE en el cliente
      citas.sort((a, b) => {
        // Ordenar por dÃ­a de la semana y luego por hora
        const ordenDias = ['lunes', 'martes', 'miercoles', 'jueves', 'viernes'];
        const diaA = ordenDias.indexOf(a.dia);
        const diaB = ordenDias.indexOf(b.dia);
        
        if (diaA !== diaB) {
          return diaA - diaB;
        }
        
        // Si es el mismo dÃ­a, ordenar por hora
        return a.hora.localeCompare(b.hora);
      });
      
      setCitasSemanaActual(citas);
      console.log('âœ… Citas cargadas desde Firestore:', citas);
      
    } catch (error) {
      console.error('âŒ Error al cargar citas:', error);
      setError('Error al cargar citas: ' + error.message);
    }
  };

  // ðŸ”¥ NUEVA FUNCIÃ“N PARA CARGAR HISTORIAL COMPLETO
  const cargarHistorialCompleto = async () => {
    try {
      setLoading(true);
      const citasRef = collection(db, 'citas');
      
      // ðŸ“‹ CONSULTA PARA TODAS LAS CITAS DEL DOCENTE (SIN FILTRO DE SEMANA)
      const q = query(
        citasRef, 
        where('docenteId', '==', user.uid)
      );
      
      const citasSnap = await getDocs(q);
      const todasLasCitas = [];
      
      citasSnap.forEach((doc) => {
        todasLasCitas.push({
          id: doc.id,
          ...doc.data()
        });
      });
      
      // ðŸ”„ ORDENAR POR FECHA DE CREACIÃ“N (MÃS RECIENTES PRIMERO)
      todasLasCitas.sort((a, b) => {
        // Ordenar por fecha de creaciÃ³n descendente
        const fechaA = new Date(a.fechaCreacion || a.fecha || 0);
        const fechaB = new Date(b.fechaCreacion || b.fecha || 0);
        return fechaB - fechaA;
      });
      
      setHistorialCitas(todasLasCitas);
      console.log('âœ… Historial completo cargado:', todasLasCitas.length, 'citas encontradas');
      
    } catch (error) {
      console.error('âŒ Error al cargar historial completo:', error);
      setError('Error al cargar historial: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const guardarCitaEnFirestore = async (citaData) => {
