import React, { useState, useEffect } from 'react';
import '../css/DashboardDocente.css';
import { useAuth } from '../context/AuthContext';
import {
  CalendarIcon,
  UserIcon,
  ClockIcon,
  DocumentTextIcon,
  ArrowRightOnRectangleIcon,
  PencilIcon,
  TrashIcon,
  CheckIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';

function DashboardDocente() {
  const [activeSection, setActiveSection] = useState('calendario');
  const { user, logout } = useAuth();

  // 🔥 DATOS DINÁMICOS DEL DOCENTE
  const docenteData = {
    name: user?.name || 'Docente',
    email: user?.email || '',
    subject: user?.materia || user?.subject || 'Materia no asignada',
    grade: user?.grado || user?.grade || 'Grado no asignado',
    phone: user?.telefono || user?.phone || 'No disponible'
  };

  // 🔄 ESTADOS PARA HORARIOS Y CITAS
  const [horarioDocente, setHorarioDocente] = useState({
    lunes: { inicio: '08:00', fin: '14:00', disponible: true },
    martes: { inicio: '08:00', fin: '14:00', disponible: true },
    miercoles: { inicio: '08:00', fin: '14:00', disponible: true },
    jueves: { inicio: '08:00', fin: '14:00', disponible: true },
    viernes: { inicio: '08:00', fin: '14:00', disponible: true }
  });

  const [citasSemanaActual, setCitasSemanaActual] = useState([]);
  const [historialCitas, setHistorialCitas] = useState([]);
  const [semanaActual, setSemanaActual] = useState('');
  
  // Estados para modales
  const [showNewCitaModal, setShowNewCitaModal] = useState(false);
  const [showEditHorarioModal, setShowEditHorarioModal] = useState(false);
  const [showEditCitaModal, setShowEditCitaModal] = useState(false);
  const [selectedCita, setSelectedCita] = useState(null);
  
  const [newCita, setNewCita] = useState({
    padre: '',
    estudiante: '',
    dia: '',
    hora: '',
    motivo: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const diasSemana = ['lunes', 'martes', 'miercoles', 'jueves', 'viernes'];

  // 🔥 FUNCIONES UTILITARIAS
  const obtenerSemanaActual = () => {
    const hoy = new Date();
    const inicioSemana = new Date(hoy);
    inicioSemana.setDate(hoy.getDate() - hoy.getDay() + 1); // Lunes
    
    const finSemana = new Date(inicioSemana);
    finSemana.setDate(inicioSemana.getDate() + 4); // Viernes
    
    const formatoFecha = (fecha) => fecha.toLocaleDateString('es-ES', { 
      day: '2-digit', 
      month: '2-digit' 
    });
    
    return `${formatoFecha(inicioSemana)} - ${formatoFecha(finSemana)}`;
  };

  const generarHorasDisponibles = (inicio, fin) => {
    const horas = [];
    const [horaInicio, minInicio] = inicio.split(':').map(Number);
    const [horaFin, minFin] = fin.split(':').map(Number);
    
    for (let h = horaInicio; h <= horaFin; h++) {
      for (let m = 0; m < 60; m += 30) {
        if (h === horaFin && m > minFin) break;
        const hora = `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}`;
        
        // Excluir horas de receso (10:30-11:00)
        if (hora !== '10:30' && hora !== '11:00') {
          horas.push(hora);
        }
      }
    }
    return horas;
  };

  const verificarDisponibilidadHora = (dia, hora) => {
    return !citasSemanaActual.some(cita => 
      cita.dia === dia && cita.hora === hora && cita.estado !== 'Cancelada'
    );
  };

  // 🔥 EFECTOS
  useEffect(() => {
    setSemanaActual(obtenerSemanaActual());
    cargarCitasSemana();
    verificarNuevaSemana();
  }, []);

  const cargarCitasSemana = () => {
    // Aquí cargarías las citas de la base de datos
    // Por ahora uso datos de ejemplo
    setCitasSemanaActual([
      {
        id: 1,
        padre: 'María González',
        estudiante: 'Pedro González',
        dia: 'lunes',
        hora: '10:00',
        estado: 'Confirmada',
        motivo: 'Revisión de calificaciones',
        fecha: '2025-06-02'
      },
      {
        id: 2,
        padre: 'Carlos Rodríguez',
        estudiante: 'Ana Rodríguez',
        dia: 'miercoles',
        hora: '09:30',
        estado: 'Confirmada',
        motivo: 'Comportamiento en clase',
        fecha: '2025-06-04'
      }
    ]);
  };

  const verificarNuevaSemana = () => {
    // Verificar si es una nueva semana y limpiar citas si es necesario
    const ultimaSemana = localStorage.getItem('ultimaSemanaDocente');
    const semanaActualStr = obtenerSemanaActual();
    
    if (ultimaSemana && ultimaSemana !== semanaActualStr) {
      // Es una nueva semana, mover citas actuales al historial
      const citasParaHistorial = citasSemanaActual.map(cita => ({
        ...cita,
        semana: ultimaSemana,
        fechaArchivo: new Date().toISOString()
      }));
      
      setHistorialCitas(prev => [...citasParaHistorial, ...prev]);
      setCitasSemanaActual([]);
      
      console.log('🔄 Nueva semana detectada, limpiando calendario...');
    }
    
    localStorage.setItem('ultimaSemanaDocente', semanaActualStr);
  };

  // 🔥 CRUD PARA CITAS
  const handleCreateCita = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      
      // Verificar disponibilidad
      if (!verificarDisponibilidadHora(newCita.dia, newCita.hora)) {
        setError('Ya hay una cita programada en ese horario');
        return;
      }

      // Verificar que el día esté disponible en el horario del docente
      if (!horarioDocente[newCita.dia]?.disponible) {
        setError('No hay disponibilidad ese día');
        return;
      }

      const nuevaCita = {
        id: Date.now(),
        ...newCita,
        estado: 'Confirmada',
        fecha: obtenerFechaDelDia(newCita.dia),
        fechaCreacion: new Date().toISOString()
      };

      setCitasSemanaActual(prev => [...prev, nuevaCita]);
      setNewCita({ padre: '', estudiante: '', dia: '', hora: '', motivo: '' });
      setShowNewCitaModal(false);
      setError('');
      
      // Aquí harías el POST a tu API
      // await fetch('/api/citas', { method: 'POST', body: JSON.stringify(nuevaCita) });
      
    } catch (error) {
      setError('Error al crear la cita: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateCita = async (citaId, datosActualizados) => {
    try {
      setLoading(true);
      
      setCitasSemanaActual(prev => 
        prev.map(cita => 
          cita.id === citaId 
            ? { ...cita, ...datosActualizados, fechaActualizacion: new Date().toISOString() }
            : cita
        )
      );
      
      setShowEditCitaModal(false);
      setSelectedCita(null);
      setError('');
      
      // API call aquí
      // await fetch(`/api/citas/${citaId}`, { method: 'PUT', body: JSON.stringify(datosActualizados) });
      
    } catch (error) {
      setError('Error al actualizar la cita: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteCita = async (citaId) => {
    try {
      setLoading(true);
      
      setCitasSemanaActual(prev => prev.filter(cita => cita.id !== citaId));
      setError('');
      
      // API call aquí
      // await fetch(`/api/citas/${citaId}`, { method: 'DELETE' });
      
    } catch (error) {
      setError('Error al eliminar la cita: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCambiarEstadoCita = async (citaId, nuevoEstado) => {
    await handleUpdateCita(citaId, { estado: nuevoEstado });
  };

  // 🔥 CRUD PARA HORARIOS
  const handleUpdateHorario = (dia, campo, valor) => {
    setHorarioDocente(prev => ({
      ...prev,
      [dia]: { ...prev[dia], [campo]: valor }
    }));
  };

  const guardarHorario = async () => {
    try {
      setLoading(true);
      
      // Validar horarios
      for (const dia of diasSemana) {
        const { inicio, fin } = horarioDocente[dia];
        if (inicio >= fin) {
          setError(`Horario inválido para ${dia}: la hora de inicio debe ser menor que la de fin`);
          return;
        }
      }
      
      setShowEditHorarioModal(false);
      setError('');
      
      // API call aquí
      // await fetch('/api/docente/horario', { method: 'PUT', body: JSON.stringify(horarioDocente) });
      
      console.log('✅ Horario guardado:', horarioDocente);
      
    } catch (error) {
      setError('Error al guardar horario: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // 🔥 FUNCIONES AUXILIARES
  const obtenerFechaDelDia = (dia) => {
    const hoy = new Date();
    const inicioSemana = new Date(hoy);
    inicioSemana.setDate(hoy.getDate() - hoy.getDay() + 1); // Lunes
    
    const indiceDia = diasSemana.indexOf(dia);
    const fechaDia = new Date(inicioSemana);
    fechaDia.setDate(inicioSemana.getDate() + indiceDia);
    
    return fechaDia.toISOString().split('T')[0];
  };

  const getCitasPorDia = (dia) => {
    return citasSemanaActual.filter(cita => cita.dia === dia);
  };

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCita({ ...newCita, [name]: value });
  };

  const formatearFecha = (fecha) => {
    return new Date(fecha).toLocaleDateString('es-ES', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  // 🔥 RENDERIZAR SECCIONES
  const renderCalendarioSemanal = () => (
    <div className="calendario-section">
      <div className="section-header">
        <div>
          <h2 className="section-title">Calendario Semanal</h2>
          <p className="semana-actual">Semana del {semanaActual}</p>
        </div>
        <button 
          className="new-cita-button"
          onClick={() => setShowNewCitaModal(true)}
          disabled={loading}
        >
          + Nueva Cita
        </button>
      </div>

      {error && (
        <div className="error-banner">
          <span>{error}</span>
          <button onClick={() => setError('')}>×</button>
        </div>
      )}
      
      <div className="calendario-grid">
        {diasSemana.map(dia => {
          const citasDelDia = getCitasPorDia(dia);
          const horarioDia = horarioDocente[dia];
          
          return (
            <div key={dia} className={`dia-card ${!horarioDia.disponible ? 'no-disponible' : ''}`}>
              <div className="dia-header">
                <h3 className="dia-nombre">{dia.charAt(0).toUpperCase() + dia.slice(1)}</h3>
                <span className="citas-count">{citasDelDia.length} citas</span>
                {horarioDia.disponible && (
                  <span className="horario-dia-info">
                    {horarioDia.inicio} - {horarioDia.fin}
                  </span>
                )}
              </div>
              
              <div className="citas-dia">
                {horarioDia.disponible ? (
                  citasDelDia.length > 0 ? (
                    citasDelDia.map(cita => (
                      <div key={cita.id} className="cita-mini">
                        <div className="cita-time">{cita.hora}</div>
                        <div className="cita-info">
                          <div className="cita-padre-mini">{cita.padre}</div>
                          <div className="cita-motivo-mini">{cita.motivo}</div>
                          <span className={`status-mini ${cita.estado.toLowerCase()}`}>
                            {cita.estado}
                          </span>
                        </div>
                        <div className="cita-actions-mini">
                          <button 
                            onClick={() => {
                              setSelectedCita(cita);
                              setShowEditCitaModal(true);
                            }}
                            className="action-mini edit"
                            title="Editar"
                          >
                            <PencilIcon className="w-3 h-3" />
                          </button>
                          <button 
                            onClick={() => handleDeleteCita(cita.id)}
                            className="action-mini delete"
                            title="Eliminar"
                          >
                            <TrashIcon className="w-3 h-3" />
                          </button>
                          {cita.estado === 'Confirmada' && (
                            <>
                              <button 
                                onClick={() => handleCambiarEstadoCita(cita.id, 'Completada')}
                                className="action-mini complete"
                                title="Completar"
                              >
                                <CheckIcon className="w-3 h-3" />
                              </button>
                              <button 
                                onClick={() => handleCambiarEstadoCita(cita.id, 'Cancelada')}
                                className="action-mini cancel"
                                title="Cancelar"
                              >
                                <XMarkIcon className="w-3 h-3" />
                              </button>
                            </>
                          )}
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="no-citas">Sin citas programadas</div>
                  )
                ) : (
                  <div className="dia-no-disponible">No disponible</div>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );

  const renderHorarios = () => (
    <div className="horarios-section">
      <div className="section-header">
        <h2 className="section-title">Mi Horario</h2>
        <button 
          className="edit-schedule-button"
          onClick={() => setShowEditHorarioModal(true)}
        >
          Modificar Horario
        </button>
      </div>
      
      <div className="horario-grid">
        {diasSemana.map(dia => {
          const horario = horarioDocente[dia];
          return (
            <div key={dia} className={`horario-card ${!horario.disponible ? 'no-disponible' : ''}`}>
              <div className="horario-dia">{dia.charAt(0).toUpperCase() + dia.slice(1)}</div>
              <div className="horario-horas">
                {horario.disponible ? `${horario.inicio} - ${horario.fin}` : 'No disponible'}
              </div>
              <div className="horario-status">
                <span className={`status-badge ${horario.disponible ? 'disponible' : 'no-disponible'}`}>
                  {horario.disponible ? 'Disponible' : 'No disponible'}
                </span>
              </div>
            </div>
          );
        })}
      </div>
      
      <div className="disponibilidad-section">
        <h3 className="subsection-title">Información sobre Horarios</h3>
        <div className="disponibilidad-info">
          <ul className="horarios-lista">
            <li>🕐 Horario base: 8:00 AM - 2:00 PM (Lunes a Viernes)</li>
            <li>⏰ Citas cada 30 minutos</li>
            <li>☕ Receso: 10:30 AM - 11:00 AM (no disponible para citas)</li>
            <li>🔄 El calendario se reinicia cada semana automáticamente</li>
            <li>📅 Las citas pasadas se archivan en el historial</li>
          </ul>
        </div>
      </div>
    </div>
  );

  const renderPerfil = () => (
    <div className="perfil-section">
      <h2 className="section-title">Mi Perfil</h2>
      
      <div className="perfil-content">
        <div className="perfil-card">
          <div className="perfil-header">
            <div className="avatar-section">
              <div className="avatar">{docenteData.name.split(' ').map(n => n[0]).join('')}</div>
              <div className="perfil-info">
                <h3 className="perfil-name">{docenteData.name}</h3>
                <p className="perfil-role">{docenteData.subject} - {docenteData.grade}</p>
              </div>
            </div>
          </div>
          
          <div className="perfil-details">
            <div className="detail-group">
              <label className="detail-label">Email:</label>
              <span className="detail-value">{docenteData.email}</span>
            </div>
            
            <div className="detail-group">
              <label className="detail-label">Teléfono:</label>
              <span className="detail-value">{docenteData.phone}</span>
            </div>
            
            <div className="detail-group">
              <label className="detail-label">Materia:</label>
              <span className="detail-value">{docenteData.subject}</span>
            </div>
            
            <div className="detail-group">
              <label className="detail-label">Grado:</label>
              <span className="detail-value">{docenteData.grade}</span>
            </div>
          </div>
          
          <button className="edit-profile-button">Editar Perfil</button>
        </div>
      </div>
    </div>
  );

  const renderHistorial = () => (
    <div className="historial-section">
      <h2 className="section-title">Historial de Citas</h2>
      
      <div className="historial-filters">
        <select className="filter-select">
          <option>Todas las citas</option>
          <option>Completadas</option>
          <option>Canceladas</option>
        </select>
        <input type="date" className="filter-date" />
      </div>
      
      <div className="citas-list">
        {historialCitas.length > 0 ? (
          historialCitas.map(cita => (
            <div key={cita.id} className="cita-card historial">
              <div className="cita-header">
                <div className="cita-info">
                  <h3 className="cita-padre">{cita.padre}</h3>
                  <p className="cita-estudiante">Estudiante: {cita.estudiante}</p>
                </div>
                <span className={`status-badge ${cita.estado.toLowerCase()}`}>
                  {cita.estado}
                </span>
              </div>
              
              <div className="cita-details">
                <div className="detail-row">
                  <span className="detail-label">Semana:</span>
                  <span className="detail-value">{cita.semana}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Día:</span>
                  <span className="detail-value">{cita.dia.charAt(0).toUpperCase() + cita.dia.slice(1)}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Hora:</span>
                  <span className="detail-value">{cita.hora}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Motivo:</span>
                  <span className="detail-value">{cita.motivo}</span>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="empty-state">
            <p>No hay citas en el historial todavía.</p>
          </div>
        )}
      </div>
    </div>
  );

  if (!user) {
    return (
      <div className="loading-container">
        <div className="loading-content">
          <div className="loading-spinner"></div>
          <p>Cargando información del docente...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Sidebar Navigation */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <div className="logo">
            <span className="logo-text">QuickReserve</span>
          </div>
          <div className="user-info">
            <div className="user-avatar">{docenteData.name.split(' ').map(n => n[0]).join('')}</div>
            <div className="user-details">
              <span className="user-name">{docenteData.name}</span>
              <span className="user-role">Docente</span>
            </div>
          </div>
        </div>
        
        <nav className="sidebar-nav">
          <button 
            className={`nav-item ${activeSection === 'calendario' ? 'active' : ''}`}
            onClick={() => setActiveSection('calendario')}
            data-tooltip="Calendario"
          >
            <CalendarIcon className="nav-icon w-6 h-6" />
            <span className="nav-text">Calendario</span>
          </button>
          
          <button 
            className={`nav-item ${activeSection === 'perfil' ? 'active' : ''}`}
            onClick={() => setActiveSection('perfil')}
            data-tooltip="Mi Perfil"
          >
            <UserIcon className="nav-icon w-6 h-6" />
            <span className="nav-text">Mi Perfil</span>
          </button>
          
          <button 
            className={`nav-item ${activeSection === 'horarios' ? 'active' : ''}`}
            onClick={() => setActiveSection('horarios')}
            data-tooltip="Horarios"
          >
            <ClockIcon className="nav-icon w-6 h-6" />
            <span className="nav-text">Horarios</span>
          </button>
          
          <button 
            className={`nav-item ${activeSection === 'historial' ? 'active' : ''}`}
            onClick={() => setActiveSection('historial')}
            data-tooltip="Historial"
          >
            <DocumentTextIcon className="nav-icon w-6 h-6" />
            <span className="nav-text">Historial</span>
          </button>
        </nav>
        
        <div className="sidebar-footer">
          <button className="logout-button" data-tooltip="Cerrar Sesión" onClick={handleLogout}>
            <ArrowRightOnRectangleIcon className="nav-icon w-6 h-6" />
            <span className="nav-text">Cerrar Sesión</span>
          </button>
        </div>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        <div className="content-wrapper">
          {activeSection === 'calendario' && renderCalendarioSemanal()}
          {activeSection === 'perfil' && renderPerfil()}
          {activeSection === 'horarios' && renderHorarios()}
          {activeSection === 'historial' && renderHistorial()}
        </div>
      </main>

      {/* Modal para nueva cita */}
      {showNewCitaModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2 className="modal-title">Programar Nueva Cita</h2>
              <button 
                className="close-button"
                onClick={() => setShowNewCitaModal(false)}
              >
                ×
              </button>
            </div>
            
            <form onSubmit={handleCreateCita} className="cita-form">
              <div className="form-group">
                <label className="form-label">Nombre del Padre/Madre</label>
                <input
                  type="text"
                  name="padre"
                  value={newCita.padre}
                  onChange={handleInputChange}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Nombre del Estudiante</label>
                <input
                  type="text"
                  name="estudiante"
                  value={newCita.estudiante}
                  onChange={handleInputChange}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">Día</label>
                  <select
                    name="dia"
                    value={newCita.dia}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                  >
                    <option value="">Seleccionar día</option>
                    {diasSemana.map(dia => (
                      <option 
                        key={dia} 
                        value={dia}
                        disabled={!horarioDocente[dia].disponible}
                      >
                        {dia.charAt(0).toUpperCase() + dia.slice(1)} 
                        {horarioDocente[dia].disponible 
                          ? ` (${horarioDocente[dia].inicio} - ${horarioDocente[dia].fin})`
                          : ' (No disponible)'
                        }
                      </option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label className="form-label">Hora</label>
                  <select
                    name="hora"
                    value={newCita.hora}
                    onChange={handleInputChange}
                    className="form-input"
                    required
                    disabled={!newCita.dia}
                  >
                    <option value="">Seleccionar hora</option>
                    {newCita.dia && horarioDocente[newCita.dia].disponible && 
                      generarHorasDisponibles(
                        horarioDocente[newCita.dia].inicio, 
                        horarioDocente[newCita.dia].fin
                      ).map(hora => (
                        <option 
                          key={hora} 
                          value={hora}
                          disabled={!verificarDisponibilidadHora(newCita.dia, hora)}
                        >
                          {hora} {!verificarDisponibilidadHora(newCita.dia, hora) && '(Ocupado)'}
                        </option>
                      ))
                    }
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Motivo de la Cita</label>
                <textarea
                  name="motivo"
                  value={newCita.motivo}
                  onChange={handleInputChange}
                  className="form-textarea"
                  rows="3"
                  required
                />
              </div>

              <div className="form-actions">
                <button 
                  type="button" 
                  className="cancel-button"
                  onClick={() => setShowNewCitaModal(false)}
                  disabled={loading}
                >
                  Cancelar
                </button>
                <button 
                  type="submit" 
                  className="submit-button"
                  disabled={loading}
                >
                  {loading ? 'Programando...' : 'Programar Cita'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Modal para editar horario */}
      {showEditHorarioModal && (
        <div className="modal-overlay">
          <div className="modal-content large">
            <div className="modal-header">
              <h2 className="modal-title">Modificar Horario Semanal</h2>
              <button 
                className="close-button"
                onClick={() => setShowEditHorarioModal(false)}
              >
                ×
              </button>
            </div>
            
            <div className="horario-form">
              <p className="form-description">
                Define tu disponibilidad para cada día de la semana (8:00 AM - 2:00 PM)
              </p>
              
              {diasSemana.map(dia => (
                <div key={dia} className="horario-day-config">
                  <div className="day-header">
                    <h3 className="day-title">{dia.charAt(0).toUpperCase() + dia.slice(1)}</h3>
                    <label className="disponible-toggle">
                      <input
                        type="checkbox"
                        checked={horarioDocente[dia].disponible}
                        onChange={(e) => handleUpdateHorario(dia, 'disponible', e.target.checked)}
                      />
                      <span className="toggle-label">Disponible</span>
                    </label>
                  </div>
                  
                  {horarioDocente[dia].disponible && (
                    <div className="horario-inputs">
                      <div className="time-input-group">
                        <label className="time-label">Inicio:</label>
                        <select
                          value={horarioDocente[dia].inicio}
                          onChange={(e) => handleUpdateHorario(dia, 'inicio', e.target.value)}
                          className="time-select"
                        >
                          <option value="08:00">08:00</option>
                          <option value="08:30">08:30</option>
                          <option value="09:00">09:00</option>
                          <option value="09:30">09:30</option>
                          <option value="10:00">10:00</option>
                        </select>
                      </div>
                      
                      <div className="time-input-group">
                        <label className="time-label">Fin:</label>
                        <select
                          value={horarioDocente[dia].fin}
                          onChange={(e) => handleUpdateHorario(dia, 'fin', e.target.value)}
                          className="time-select"
                        >
                          <option value="12:00">12:00</option>
                          <option value="12:30">12:30</option>
                          <option value="13:00">13:00</option>
                          <option value="13:30">13:30</option>
                          <option value="14:00">14:00</option>
                        </select>
                      </div>
                    </div>
                  )}
                </div>
              ))}

              <div className="form-actions">
                <button 
                  type="button" 
                  className="cancel-button"
                  onClick={() => setShowEditHorarioModal(false)}
                  disabled={loading}
                >
                  Cancelar
                </button>
                <button 
                  type="button" 
                  className="submit-button"
                  onClick={guardarHorario}
                  disabled={loading}
                >
                  {loading ? 'Guardando...' : 'Guardar Horario'}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal para editar cita */}
      {showEditCitaModal && selectedCita && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2 className="modal-title">Editar Cita</h2>
              <button 
                className="close-button"
                onClick={() => setShowEditCitaModal(false)}
              >
                ×
              </button>
            </div>
            
            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.target);
              const datosActualizados = {
                padre: formData.get('padre'),
                estudiante: formData.get('estudiante'),
                motivo: formData.get('motivo'),
                estado: formData.get('estado')
              };
              handleUpdateCita(selectedCita.id, datosActualizados);
            }} className="cita-form">
              
              <div className="form-group">
                <label className="form-label">Nombre del Padre/Madre</label>
                <input
                  type="text"
                  name="padre"
                  defaultValue={selectedCita.padre}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Nombre del Estudiante</label>
                <input
                  type="text"
                  name="estudiante"
                  defaultValue={selectedCita.estudiante}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">Día</label>
                  <input
                    type="text"
                    value={selectedCita.dia.charAt(0).toUpperCase() + selectedCita.dia.slice(1)}
                    className="form-input"
                    disabled
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Hora</label>
                  <input
                    type="text"
                    value={selectedCita.hora}
                    className="form-input"
                    disabled
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Estado</label>
                <select
                  name="estado"
                  defaultValue={selectedCita.estado}
                  className="form-input"
                  required
                >
                  <option value="Confirmada">Confirmada</option>
                  <option value="Completada">Completada</option>
                  <option value="Cancelada">Cancelada</option>
                  <option value="Reprogramada">Reprogramada</option>
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Motivo de la Cita</label>
                <textarea
                  name="motivo"
                  defaultValue={selectedCita.motivo}
                  className="form-textarea"
                  rows="3"
                  required
                />
              </div>

              <div className="form-actions">
                <button 
                  type="button" 
                  className="cancel-button"
                  onClick={() => setShowEditCitaModal(false)}
                  disabled={loading}
                >
                  Cancelar
                </button>
                <button 
                  type="submit" 
                  className="submit-button"
                  disabled={loading}
                >
                  {loading ? 'Actualizando...' : 'Actualizar Cita'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default DashboardDocente;
