import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/Register.css';

function Register() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'padre',
  });

  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    // Limpiar errores cuando el usuario empiece a escribir
    if (error) setError('');
  };

  // Función para limpiar el formulario
  const clearForm = () => {
    setFormData({
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
      role: 'padre',
    });
  };

  // Función para enviar correo de bienvenida
  const sendWelcomeEmail = async (userData) => {
    try {
      await fetch('http://localhost:5000/api/auth/send-welcome-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: userData.email,
          name: userData.name,
          role: userData.role,
          tempPassword: formData.password // Solo para el correo de bienvenida
        }),
      });
    } catch (error) {
      console.error('Error al enviar correo de bienvenida:', error);
      // No bloqueamos el registro si falla el correo
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Validaciones básicas
    if (formData.password !== formData.confirmPassword) {
      setError('Las contraseñas no coinciden');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('La contraseña debe tener al menos 6 caracteres');
      setLoading(false);
      return;
    }

    try {
      // 1. Registrar usuario
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        // 2. Enviar correo de bienvenida
        await sendWelcomeEmail(data);

        // 3. Limpiar formulario
        clearForm();

        // 4. Mostrar mensaje de éxito
        setSuccessMessage(`¡Bienvenido/a ${data.name}! Tu cuenta ha sido creada exitosamente. Se ha enviado un correo de confirmación a ${data.email}.`);

        // 5. Guardar datos para acceso inmediato (opcional)
        // Si quieres que el usuario acceda automáticamente después del registro
        localStorage.setItem('userData', JSON.stringify(data));

        // 6. Redirigir al dashboard después de 3 segundos
        setTimeout(() => {
          if (data.role === 'docente') {
            navigate('/dashboard/docente');
          } else if (data.role === 'padre') {
            navigate('/dashboard/padre');
          } else {
            navigate('/login'); // Si no reconoce el rol, va al login
          }
        }, 3000);

      } else {
        setError(data.message || 'No se pudo registrar el usuario');
      }
    } catch (error) {
      console.error('Error al enviar el formulario:', error);
      setError('Hubo un error en el servidor. Inténtalo de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-register">
      <form onSubmit={handleSubmit} className="register-form">
        <h2 className="register-title">Formulario de Registro</h2>

        {/* Mensaje de éxito */}
        {successMessage && (
          <div className="success-message">
            <p>{successMessage}</p>
            <div className="loading-spinner-small"></div>
            <p className="redirect-info">Redirigiendo a tu dashboard...</p>
          </div>
        )}

        {/* Mensaje de error */}
        {error && (
          <div className="error-message">
            <p>{error}</p>
          </div>
        )}

        {/* Solo mostrar formulario si no hay mensaje de éxito */}
        {!successMessage && (
          <>
            <input
              type="text"
              name="name"
              placeholder="Nombre completo"
              value={formData.name}
              onChange={handleChange}
              className="register-input"
              required
              disabled={loading}
            />

            <input
              type="email"
              name="email"
              placeholder="Correo electrónico"
              value={formData.email}
              onChange={handleChange}
              className="register-input"
              required
              disabled={loading}
            />

            <input
              type="password"
              name="password"
              placeholder="Contraseña (mínimo 6 caracteres)"
              value={formData.password}
              onChange={handleChange}
              className="register-input"
              required
              disabled={loading}
            />

            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirmar contraseña"
              value={formData.confirmPassword}
              onChange={handleChange}
              className="register-input"
              required
              disabled={loading}
            />

            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="register-input"
              disabled={loading}
            >
              <option value="padre">Padre de Familia</option>
              <option value="docente">Docente</option>
            </select>

            <button
              type="submit"
              className="register-button"
              disabled={loading}
            >
              {loading ? 'Registrando...' : 'Registrarse'}
            </button>

            <p className="register-footer">
              ¿Ya tienes usuario? <a href="/login">Inicia sesión</a>
            </p>
          </>
        )}
      </form>
    </div>
  );
}

export default Register;
