import React, { useState, useEffect } from 'react';
import '../css/DocenteDashboard.css';

function DocenteDashboard({ docente }) {
  // Estado para próximas citas simuladas
  const [citas, setCitas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Simulación de carga de datos (puedes sustituirlo con fetch o axios)
  useEffect(() => {
    setLoading(true);
    setError('');
    // Simulamos delay
    setTimeout(() => {
      // Simulamos datos de citas
      const citasSimuladas = [
        {
          id: 1,
          estudiante: 'Juan Pérez',
          fecha: '2025-06-05',
          hora: '10:00 AM',
          motivo: 'Consulta sobre examen',
          estado: 'Confirmada',
        },
        {
          id: 2,
          estudiante: 'María López',
          fecha: '2025-06-06',
          hora: '02:00 PM',
          motivo: 'Revisión de proyecto',
          estado: 'Pendiente',
        },
      ];
      setCitas(citasSimuladas);
      setLoading(false);
    }, 1500);
  }, []);

  return (
    <div className="docente-dashboard-container">
      <h1 className="docente-dashboard-title">Panel de Docente</h1>

      <div className="docente-dashboard-content">
        <p className="docente-welcome">
          Bienvenido, <strong>{docente?.name || 'Docente'}</strong> — Rol: {docente?.role || 'docente'}
        </p>

        <section>
          <h2 style={{ color: '#d93025', fontWeight: '700', marginBottom: '1rem' }}>Próximas Citas</h2>

          {loading && <p>Cargando citas...</p>}
          {error && <p style={{ color: 'red' }}>Error: {error}</p>}

          {!loading && citas.length === 0 && <p>No tienes citas próximas.</p>}

          <div className="dashboard-list">
            {citas.map((cita) => (
              <div key={cita.id} className="dashboard-card">
                <h3 className="card-title">{cita.estudiante}</h3>
                <p className="card-text">
                  <strong>Fecha:</strong> {cita.fecha} <br />
                  <strong>Hora:</strong> {cita.hora} <br />
                  <strong>Motivo:</strong> {cita.motivo} <br />
                  <strong>Estado:</strong> {cita.estado}
                </p>
              </div>
            ))}
          </div>
        </section>

        <section style={{ marginTop: '2rem', display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
          <button className="dashboard-button" onClick={() => alert('Ir a gestionar horarios')}>
            Gestionar Horarios
          </button>
          <button className="dashboard-button" onClick={() => alert('Ver historial de citas')}>
            Historial de Citas
          </button>
          <button className="dashboard-button" onClick={() => alert('Editar perfil')}>
            Editar Perfil
          </button>
        </section>
      </div>
    </div>
  );
}

export default DocenteDashboard;
