import './App.css';
import { Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext'; // Ajusta la ruta seg√∫n tu estructura
import ProtectedRoute from './context/ProtectedRoute'; // Ajusta la ruta seg√∫n tu estructura
import Home from './views/Home';
import Login from './views/Login';
import Register from './views/Register';
import DashboardDocente from './views/DashboardDocente';
import DashboardAdministrador from './views/DashboardAdministrador';
import DashboardPadre from './views/DashboardPadre';

// üî• COMPONENTE PARA REDIRIGIR AUTOM√ÅTICAMENTE SEG√öN EL ROL
import { useAuth } from './context/AuthContext';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function DashboardRedirect() {
  const { user, loading } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    console.log('üîç Dashboard - Estado actual:', { 
      user, 
      loading, 
      userRole: user?.role,
      userType: user?.type 
    });

    if (!loading && user) {
      const userRole = user.role || user.type;
      console.log('üöÄ Redirigiendo usuario con rol:', userRole);
      
      switch (userRole) {
        case 'padre':
          console.log('üì± Redirigiendo a dashboard-padre');
          navigate('/dashboard-padre', { replace: true });
          break;
          
        case 'docente':
          console.log('üë®‚Äçüè´ Redirigiendo a dashboard-docente');
          navigate('/dashboard-docente', { replace: true });
          break;
          
        case 'administrador':
          console.log('‚ö° Redirigiendo a dashboard-administrador');
          navigate('/dashboard-administrador', { replace: true });
          break;
          
        default:
          console.error('‚ùå Rol no reconocido:', userRole);
          console.log('üë§ Datos completos del usuario:', user);
          // Por defecto, mostrar mensaje de error
          break;
      }
    }
  }, [user, loading, navigate]);

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        flexDirection: 'column'
      }}>
        <div style={{
          width: '50px',
          height: '50px',
          border: '5px solid #f3f3f3',
          borderTop: '5px solid #3498db',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite'
        }}></div>
        <p style={{ marginTop: '20px', color: '#666' }}>
          Cargando informaci√≥n del usuario...
        </p>
        <style>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      flexDirection: 'column'
    }}>
      <h2>Dashboard General</h2>
      <p>Determinando tipo de usuario...</p>
      {user && (
        <div style={{ marginTop: '20px', padding: '20px', background: '#f5f5f5', borderRadius: '8px' }}>
          <p><strong>Debug Info:</strong></p>
          <p>Nombre: {user.name}</p>
          <p>Email: {user.email}</p>
          <p>Role: {user.role}</p>
          <p>Type: {user.type}</p>
          <pre style={{ fontSize: '12px', marginTop: '10px' }}>
            {JSON.stringify(user, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <div className="App">
        <Routes>
          {/* Rutas p√∫blicas */}
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          
          {/* Rutas protegidas con roles espec√≠ficos */}
          <Route 
            path="/dashboard-docente" 
            element={
              <ProtectedRoute allowedRoles={['docente']}>
                <DashboardDocente />
              </ProtectedRoute>
            } 
          />
          
          <Route 
            path="/dashboard-administrador" 
            element={
              <ProtectedRoute allowedRoles={['administrador']}>
                <DashboardAdministrador />
              </ProtectedRoute>
            } 
          />
          
          <Route 
            path="/dashboard-padre" 
            element={
              <ProtectedRoute allowedRoles={['padre']}>
                <DashboardPadre />
              </ProtectedRoute>
            } 
          />

          {/* Ruta para cualquier usuario autenticado - AHORA CON REDIRECCI√ìN AUTOM√ÅTICA */}
          <Route 
            path="/dashboard" 
            element={
              <ProtectedRoute>
                <DashboardRedirect />
              </ProtectedRoute>
            } 
          />
        </Routes>
      </div>
    </AuthProvider>
  );
}

export default App;
