import React, { useState } from 'react';
import '../css/Login.css';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    remember: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({ ...formData, [name]: type === 'checkbox' ? checked : value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Login data:', formData);
  };

  return (
    <div className="login-page">
      {/* Sección izquierda - Formulario */}
      <div className="login-form-section">
        <h2 className="login-title">Welcome back!</h2>
        <p className="login-subtitle">Enter to get unlimited access to data & information.</p>

        <form onSubmit={handleSubmit}>
          <label>Email *</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="login-input"
            placeholder="Enter your mail address"
            required
          />

          <label>Password *</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="login-input"
            placeholder="Enter password"
            required
          />

          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
            <label style={{ fontSize: '0.9rem', color: '#555' }}>
              <input
                type="checkbox"
                name="remember"
                checked={formData.remember}
                onChange={handleChange}
              />{' '}
              Remember me
            </label>
          </div>

          <button type="submit" className="login-button">Log In</button>

          <div className="login-footer">Or login with</div>

          <button type="button" className="google-button">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg"
              alt="Google"
              style={{ width: '20px', height: '20px', marginRight: '8px' }}
            />
            Sign in with Google
          </button>

          <p className="login-footer">
            Don’t have an account? <a href="/register">Register here</a>
          </p>
        </form>
      </div>

      {/* Sección derecha - Imagen */}
      <div className="login-image-section">
        <img src="/assets/login-illustration.png" alt="Login Illustration" />
      </div>
    </div>
  );
}

export default Login;
