import React, { useState } from 'react';
import './Schedule.css';

function Schedule() {
  const days = ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes'];
  const hours = ['8:00', '9:00', '10:00', '11:00', '12:00', '13:00', '14:00'];
  const [selectedDay, setSelectedDay] = useState(0); // Para vista móvil, mostrar un día a la vez

  return (
    <section className="schedule">
      <h2>Agenda una nueva cita</h2>
      
      {/* Selector de día para móvil */}
      <div className="day-selector">
        {days.map((day, index) => (
          <button 
            key={index} 
            className={`day-button ${selectedDay === index ? 'active' : ''}`}
            onClick={() => setSelectedDay(index)}
          >
            {day}
          </button>
        ))}
      </div>
      
      {/* Grid de horarios para desktop */}
      <div className="time-grid desktop-grid">
        {/* Encabezados: días */}
        {days.map((day, index) => (
          <div key={index} className="day">{day}</div>
        ))}

        {/* Filas de horas */}
        {hours.map((hour, i) => (
          <React.Fragment key={`row-${i}`}>
            {days.map((_, j) => (
              <div key={`slot-${i}-${j}`} className="time-slot">{hour}</div>
            ))}
          </React.Fragment>
        ))}
      </div>
      
      {/* Grid de horarios para móvil - solo muestra un día a la vez */}
      <div className="time-grid mobile-grid">
        <div className="day">{days[selectedDay]}</div>
        
        {hours.map((hour, i) => (
          <div key={`mobile-slot-${i}`} className="time-slot">{hour}</div>
        ))}
      </div>
    </section>
  );
}

export default Schedule;
